var siteName=document.getElementById("sitename"),siteURL=document.getElementById("siteurl"),data=document.getElementById("tabledata"),siteList=[];function addURL(){var siteData={siteName:siteName.value,siteURL:siteURL.value};siteList.push(siteData),displaySite(),cleardata()}function displaySite(){var wallet="";for(let index=0;index<siteList.length;index++)wallet+=`<tr>\n        <td>${siteList[index].siteName}</td>\n        <td><button class="btn btn-primary" type="button">Visit</button></td>\n        <td><button class="btn btn-danger" type="button" onclick="deletedata(${index})">Delete</button></td>\n        <td><button class="btn btn-warning" type="button">Update</button></td>\n\n      </tr>`;data.innerHTML=wallet}function cleardata(){siteName.value=null,siteURL.value=null}function deletedata(index){siteList.splice(index,1),displaySite()}